import {TextInput,color} from "react-native"
import './App.css';
import _, { parseInt } from "lodash"
import {useState,Component} from "react"
class Menu extends React.Component {
  menuList = [
    {
      title: 'Home',
      href: '#home'
    },
    {
      title: 'Portfolio',
      href: '#portfolio'
    },
    {
      title: 'About',
      href: '#about'
    },
    {
      title: 'Contact',
      href: '#contact'
    }
  ];
}
const DEF_TXT = 
`         25\t2019-11-25	08:30:00 Normal	=> 00:00:00 - 10:05:58 = 10:05:58 
					=> 10:06:58 - 11:52:37 = 01:45:39 
					=> 12:02:12 - 13:05:54 = 01:03:42 
					=> 15:38:20 - 16:26:21 = 00:48:01 
					=> 16:36:15 - 18:39:36 = 02:03:21 
					=> 20:19:46 - 20:51:35 = 00:31:49 
					=> 21:33:47 - 23:11:51 = 01:38:04`;

function App() {
  let zer = "";
  let [result,setResult]= useState("");
  return (
    <div className="App">
      <header 
      style={{height:50,width:1840,borderStyle:"solid",borderWidth:2 }}
      >
        <h2>HeaderExample</h2>
      </header>
      <TextInput
        multiline={true}
        numberOfLines={10}
        style={{ height: 500,width:950,borderStyle: "solid", borderWidth:2 }} 
        onChange={function(event){
            zer=event.target.value;
            console.log(zer)
      }
      }>
        {zer}
      </TextInput>
      <button onClick={function(){
        console.log(result);
        let strings = zer.split("\n");
        let times = [];
        let sum = 0;
        let days = 0;
        _.each(strings, s => {
          let t;
          if (s.split(" = ")[1]) {
            let tResult = s.split(" = ")[1];

            t = tResult.split(":");
            let t0 = parseInt(t[0]);
            if (t0 < 0) t0 = t0 + 24;

            t = parseInt(t[2]) + parseInt(t[1]) * 60 + t0 * 60 * 60;
            sum += t;
            times.push(t);
          }
          console.log(s.split(" ").toString(),'?')
          console.log(s.split(" ")[1],'?')
          if (s.split(/\t| /)[1]) {
            console.log('wowo')
            let date = new Date(Date.parse(s.split(/\t| /)[1]));
            let day = date.getDay();
            if (day !== 0 && day !== 6) {
              days=days+1;
            }
          }
        });
        let actualTime= new Date(sum*1000);
        let actHour = (actualTime.getUTCHours() + (actualTime.getUTCDate()-1) * 24).toString()
        let actMin = actualTime.getUTCMinutes().toString()
        let actSec = actualTime.getUTCSeconds().toString()
        console.log(days,"dny")
        console.log(actualTime)
        console.log(actHour,"часы")
        setResult({
          totalTime: (actHour + ":" + actMin + ":" + actSec),
           daysCount: days,
          hours_norma: days * 8.5
         });
      }}>Нажми меня!</button>
      <div className="menu">
        <ul>
          {
            this.menuList.map((item, index) => (
              <li key={index} onClick={() => this.setState({ activeIndex: index })}>
                <a class={this.state.activeIndex === index ? "menu-active" : ""} href="#home">{item.title}</a>
              </li>
            ))
          }
        </ul>
      </div>
      <div id="totalTime">{result.totalTime}</div>
      <div id="daysCount">{result.daysCount}</div>
      <div id="hours_norma">{result.hours_norma}</div>
    </div>

  );
}

export default App;
