import {TextInput} from "react-native"
import './App.css';
import _, { parseInt } from "lodash"
import {useState} from "react"

function App() {
  let zer ="8 2023-11-08  08:30:00 Normal  => 23:55:42 - 00:13:01 = -24:17:19";
  let [result,setResult]= useState("");
  return (
    <div className="App">
      <TextInput
        multiline={true}
        numberOfLines={10}
        style={{ height: 500,width:950,borderStyle: "solid", borderWidth:2 }} 
        onChange={function(event){
            zer=event.target.value;
            console.log(zer)
      }
      }>
        {zer}
      </TextInput>
      <button onClick={function(){
        console.log(result);
        let strings = zer.split("\n");
        let times = [];
        let sum = 0;
        let days = [];
        _.each(strings, s => {
          // 3  2023-11-03  08:30:00 Normal  => 02:42:28 - 05:17:56 = -23:35:28
          // 3  2023-11-03  08:30:00 Normal  => 02:42:28 - 05:17:56

          let t;
          if (s.split(" = ")[1]) {
            let tResult = s.split(" = ")[1];

            t = tResult.split(":");
            let t0 = parseInt(t[0]);
            if (t0 < 0) t0 = t0 + 24;

            t = parseInt(t[2]) + parseInt(t[1]) * 60 + t0 * 60 * 60;
            sum += t;
            times.push(t);
          }
          console.log(s.split(" "),'?')
          if (s.split("	")[1]) {
            let date = new Date(Date.parse(s.split("	")[1]));
            let day = date.getDay();
            if (day != 0 && day != 6) {
              days.push(date);
            }
          }
        });
        let actualTime= new Date(sum*1000).toISOString();
        console.log(actualTime.substring(actualTime.length -13, actualTime.length - 5));
                // let h = Math.floor(sum / (60 * 60));
        // let m = Math.floor(s / 60);
        // let s = (sum % (60 * 60));

        // s = s - m * 60;
        //console.log(h, ":", m, ":", s);
        //console.log("days count", days.length);
        // console.log("hours norma", days.length * 8.5);

        setResult({
          totalTime: actualTime.substring(actualTime.length - 13, actualTime.length - 5),
           daysCount: days.length,
           hours_norma: days.length * 8.5
         });

      }}>Нажми меня!</button>
      <div id="totalTime">{result.totalTime}</div>
      <div id="daysCount">{result.daysCount}</div>
      <div id="hours_norma">{result.hours_norma}</div>
    </div>

  );
}

export default App;
